namespace Sfynx\TranslatorBundle\Repository;

use Sfynx\CmfBundle\Entity\Page;
use Sfynx\CoreBundle\Repository\TranslationRepository;

/**
 * Word Repository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 * 
 * @subpackage Translator
 * @package    Repository
 * @author     Etienne de Longeaux <etienne.delongeaux@gmail.com>
 */
class WordRepository extends TranslationRepository
{
    /**
     * Return all words which are not archived.
     *
     * @return Page
     * @access public
     * @author Etienne de Longeaux <etienne.delongeaux@gmail.com>
     * @since  2012-01-23
     */
    public function getAllWords()
    {
        $query = $this->createQueryBuilder('a')
        ->select('a')
        ->where('a.archived = 0');
    
        return $query;
    }   
}
